/**
 * Function that sets the menu
 *
 * @access public
 * @since 3.0
 * @return boolean true
 */
function sp_set_demo_menus() {
	// get created menus
	$menus = wp_get_nav_menus();

	if ( ! empty( $menus ) ) {
		foreach( $menus as $menu ) {
			if ( isset( $menu ) && is_object( $menu ) )
				$menu_locations[$menu->slug] = $menu->term_id;
		}
	}

	// update theme mod
	set_theme_mod( 'nav_menu_locations', $menu_locations );

	// update menu meta for mega menus
	$the_slug = 'mega-menu';
	
	$args = array(
		'name' => $the_slug,
		'post_type' => 'nav_menu_item',
		'posts_per_page' => 1
	);

	$menu_item = get_posts( $args );
	
	if ( $menu_item )
		update_post_meta( $menu_item[0]->ID, '_menu-item-sp-mega-menu-enable', 'on' );

	$the_slug = 'shortcodes';
	
	$args = array(
		'name' => $the_slug,
		'post_type' => 'nav_menu_item',
		'posts_per_page' => 1
	);

	$menu_item = get_posts( $args );
	
	if ( $menu_item )
		update_post_meta( $menu_item[0]->ID, '_menu-item-sp-mega-menu-enable', 'on' );

	return true;
}

/**
 * Function that sets the woo pages after pages have been imported
 *
 * @access public
 * @since 3.0
 * @return boolean true
 */
function sp_set_demo_woo_pages() {
	update_option( 'woocommerce_shop_page_id', sp_get_page_id_from_slug( 'shop' ) );
	update_option( 'woocommerce_cart_page_id', sp_get_page_id_from_slug( 'cart' ) );
	update_option( 'woocommerce_checkout_page_id', sp_get_page_id_from_slug( 'checkout' ) );
	update_option( 'woocommerce_myaccount_page_id', sp_get_page_id_from_slug( 'my-account' ) );

	return true;
}

/**
 * Function that imports theme settings.
 *
 * @access public
 * @since 3.0
 * @return boolean true
 */
function sp_set_demo_theme_settings() {
$settings = '
