function sp_set_demo_sliders() {
$settings = '
';

	// Base64 encoded, serialized slider export code
	$sample_slider = json_decode(base64_decode( $settings ), true);

	// Iterate over the sliders
	foreach($sample_slider as $sliderkey => $slider) {

		// Iterate over the layers
		foreach($sample_slider[$sliderkey]['layers'] as $layerkey => $layer) {

			// Change background images if any
			if(!empty($sample_slider[$sliderkey]['layers'][$layerkey]['properties']['background'])) {
				$sample_slider[$sliderkey]['layers'][$layerkey]['properties']['background'] = get_template_directory_uri() . '/theme-functions/sample-data/layerslider-images/' .basename($layer['properties']['background']);
			}

			// Change thumbnail images if any
			if(!empty($sample_slider[$sliderkey]['layers'][$layerkey]['properties']['thumbnail'])) {
				$sample_slider[$sliderkey]['layers'][$layerkey]['properties']['thumbnail'] = get_template_directory_uri() . '/theme-functions/sample-data/layerslider-images/' .basename($layer['properties']['thumbnail']);
			}

			// Iterate over the sublayers
			if(isset($layer['sublayers']) && !empty($layer['sublayers'])) {
				foreach($layer['sublayers'] as $sublayerkey => $sublayer) {

					// Only IMG sublayers
					if($sublayer['type'] == 'img') {
						$sample_slider[$sliderkey]['layers'][$layerkey]['sublayers'][$sublayerkey]['image'] = get_template_directory_uri() . '/theme-functions/sample-data/layerslider-images/' .basename($sublayer['image']);
					}
				}
			}
		}
	}

	// Get WPDB Object
	global $wpdb, $home_slide_id, $home_alternate_slide_id;

	// Table name
	$table_name = $wpdb->prefix . "layerslider";

	// Append duplicate
	foreach($sample_slider as $key => $val) {

		// Insert the duplicate
		$wpdb->query(
			$wpdb->prepare("INSERT INTO $table_name
								(name, data, date_c, date_m)
							VALUES (%s, %s, %d, %d)",
							$val['properties']['title'],
							json_encode($val),
							time(),
							time()
			)
		);
	}

	$home_slide_id = $wpdb->insert_id;

$settings = '
';

	// Base64 encoded, serialized slider export code
	$sample_slider = json_decode(base64_decode( $settings ), true);

	// Iterate over the sliders
	foreach($sample_slider as $sliderkey => $slider) {

		// Iterate over the layers
		foreach($sample_slider[$sliderkey]['layers'] as $layerkey => $layer) {

			// Change background images if any
			if(!empty($sample_slider[$sliderkey]['layers'][$layerkey]['properties']['background'])) {
				$sample_slider[$sliderkey]['layers'][$layerkey]['properties']['background'] = get_template_directory_uri() . '/theme-functions/sample-data/layerslider-images/' .basename($layer['properties']['background']);
			}

			// Change thumbnail images if any
			if(!empty($sample_slider[$sliderkey]['layers'][$layerkey]['properties']['thumbnail'])) {
				$sample_slider[$sliderkey]['layers'][$layerkey]['properties']['thumbnail'] = get_template_directory_uri() . '/theme-functions/sample-data/layerslider-images/' .basename($layer['properties']['thumbnail']);
			}

			// Iterate over the sublayers
			if(isset($layer['sublayers']) && !empty($layer['sublayers'])) {
				foreach($layer['sublayers'] as $sublayerkey => $sublayer) {

					// Only IMG sublayers
					if($sublayer['type'] == 'img') {
						$sample_slider[$sliderkey]['layers'][$layerkey]['sublayers'][$sublayerkey]['image'] = get_template_directory_uri() . '/theme-functions/sample-data/layerslider-images/' .basename($sublayer['image']);
					}
				}
			}
		}
	}

	// Table name
	$table_name = $wpdb->prefix . "layerslider";

	// Append duplicate
	foreach($sample_slider as $key => $val) {

		// Insert the duplicate
		$wpdb->query(
			$wpdb->prepare("INSERT INTO $table_name
								(name, data, date_c, date_m)
							VALUES (%s, %s, %d, %d)",
							$val['properties']['title'],
							json_encode($val),
							time(),
							time()
			)
		);
	}

	$home_alternate_slide_id = $wpdb->insert_id;

	return true;
}

function sp_set_demo_page_builder_modules() {
	global $home_slide_id, $home_alternate_slide_id;

	$mens_term_obj = get_term_by( 'slug', 'mens', 'product_cat' );
	
	if ( is_object( $mens_term_obj ) )
		$mens_cat_id = $mens_term_obj->term_id;
	else
		$mens_cat_id = '';

	$womens_term_obj = get_term_by( 'slug', 'womens', 'product_cat' );
	
	if ( is_object( $womens_term_obj ) )	
		$womens_cat_id = $womens_term_obj->term_id;
	else
