		$portfolio_cat_id = '';

	$home = get_post_meta( sp_get_page_id_from_slug( 'home' ), '_sp_page_builder_modules', true );

	if ( isset( $home ) ) {
		$home = maybe_unserialize( base64_decode( $home ) );

