/**
 * Function that handles data import of sample data
 *
 * @access public
 * @since 3.0
 * @return string $response
 */
function sp_import_data() {
	global $wpdb;
	
	// delete all menu items
	$wpdb->query( $wpdb->prepare( "DELETE FROM {$wpdb->posts} WHERE post_type = %s", "nav_menu_item" ) );

	// set the theme settings before import
	sp_set_demo_theme_settings();

	// define importers if not defined
	if ( ! defined( 'WP_LOAD_IMPORTERS' ) ) define( 'WP_LOAD_IMPORTERS', true );

	// load the importer script
	require_once ABSPATH . 'wp-admin/includes/import.php';

	$importer_error = false;
	$response = '';

	if ( ! class_exists( 'WP_Importer' ) ) {
		$class_wp_importer = ABSPATH . 'wp-admin/includes/class-wp-importer.php';

		if ( file_exists( $class_wp_importer ) )
			require_once( $class_wp_importer );
		else
			$importer_error = true;
	}

	if ( ! class_exists( 'WP_Import' ) ) {
		$class_wp_import = THEME_PATH . 'sp-framework/inc/wordpress-importer/wordpress-importer.php';
		
		if ( file_exists( $class_wp_import ) )
			require_once( $class_wp_import );
		else
			$importer_error = true;
	}

	if ( $importer_error ) {
		$response .= __( 'Auto Importing could not be completed.  Please use the WordPress importer plugin to manually import your sample data.  The XML file is located in your themes folder/theme-functions/sample_data/', 'sp-theme' );
	} else {
		if( ! is_file( THEME_PATH . 'theme-functions/sample-data/sample_data.xml' ) ) {
			$response .= __( 'The XML file containing the sample data is not available or could not be read in <pre>' . THEME_PATH . 'theme-functions/sample-data/sample_data.xml' . '</pre> Please use the WordPress importer plugin to manually import your sample data.  The XML file is located in your themes folder/theme-functions/sample-data/', 'sp-theme' );
		} else {
			$wp_import = new wp_import();
			$wp_import->fetch_attachments = true;
			$wp_import->import( THEME_PATH . 'theme-functions/sample-data/sample_data.xml' );
		}
	}

	// set menu
	sp_set_demo_menus();

	// set woo pages
	sp_set_demo_woo_pages();	

	// change show on front to page
	update_option( 'show_on_front', 'page' );

	// set the page to home
	update_option( 'page_on_front', sp_get_page_id_from_slug( 'home' ) );

	// set woo shop page displays
	update_option( 'woocommerce_shop_page_display', '' );

	// set the layer sliders
	sp_set_demo_sliders();

	// update page builder modules
	sp_set_demo_page_builder_modules();

	// delete the customizer css file
	_sp_delete_customizer_styles_css();

	// delete the font css file
	_sp_delete_font_styles_css();

	// delete the custom styles css file
	_sp_delete_custom_styles_css();
	
	return $response;
}